<?phpnamespace Admin\Model;use Think\Model;class RoleuserModel extends Model {    protected $tableName = 'role_user';    //自动验证    protected $_validate = array(        //array(验证字段,验证规则,错误提示,验证条件,附加规则,验证时间)        array('name', 'require', '角色名称不能为空！', 1, 'regex', self::MODEL_BOTH),    );    protected $_auto = array(        array('create_time','time',1,'function'),        array('update_time','time',2,'function'),    );    /**     * 通过user_id 获取角色ID     * @param $uid     * @return mixed     */    public function toRoleIds($uid){        return $this->where('user_id = '.$uid)->getField('role_id',true);    }}