<?phpnamespace Storemanagement\Model;use Think\Model;class CanmingModel extends Model{    protected $_validate = array(        array('cm_name','require','请输入菜品名称',1,'regex',3),        array('canping_id','require','请选择菜品分类',1,'regex',3),        array('now_price','require','请输入菜品价格',1,'regex',3),        array('sort_order','number','排序必须是数字',2,'regex',3),    );    protected function getPrice(){        $oprice = I('post.original_price');        $nprice = I('post.now_price');        if($oprice <= 0 || $nprice <= 0){            return false;        }    }    protected function getCmname(){        $cm_name = I('post.cm_name');        $id = session('user_auth.uid');        $canming = M('Canming');        $store = M('Store');        $store_id = $store->where('uid = '.$id)->field("store_id")->find();        $where['store_id'] = $store_id['store_id'];        $where['cm_name'] = $cm_name;        $clist = $canming->where($where)->count();        if($clist > 0){            return false;        }    }}